selector_to_html = {"a[href=\"#glossary\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Glossary<a class=\"headerlink\" href=\"#glossary\" title=\"Permalink to this heading\">#</a></h1>", "a[href=\"#term-image-length\"]": "<dt id=\"term-image-length\">image-length</dt><dd><p>The number of pixels on the long axis of the raw <code class=\"code docutils literal notranslate\"><span class=\"pre\">.tiff</span></code> file.</p></dd>", "a[href=\"#term-image-width\"]": "<dt id=\"term-image-width\">image-width</dt><dd><p>The number of pixels on the short, slow-galvo axis of the raw <code class=\"code docutils literal notranslate\"><span class=\"pre\">.tiff</span></code> file.</p></dd>", "a[href=\"#term-deconvolution\"]": "<dt id=\"term-deconvolution\">deconvolution</dt><dd><p>The process performed after segmentation to the resulting traces to infer spike times from flourescence values.</p></dd>", "a[href=\"#term-pixel-resolution\"]": "<dt id=\"term-pixel-resolution\">pixel-resolution</dt><dd><p>The length of each pixel, in micron (px/um).</p></dd>", "a[href=\"#term-mean_img\"]": "<dt id=\"term-mean_img\">mean_img</dt><dd><p>The average of the registered movie over time. Used as a baseline representation of the structural signal.</p></dd>", "a[href=\"#term-roi_height_px\"]": "<dt id=\"term-roi_height_px\">roi_height_px</dt><dd><p>The size of the <a class=\"reference internal\" href=\"#term-region-of-interest\"><span class=\"xref std std-term\">region-of-interest</span></a> on its longest dimension.</p></dd>", "a[href=\"#term-skewness\"]": "<dt id=\"term-skewness\">skewness </dt><dd><p>How often do you get an outlier in the pixel activity across time, good for calcium indicators because theyre flat relative to a few events over the course of a recording.</p></dd>", "a[href=\"#term-seed\"]": "<dt id=\"term-seed\">seed</dt><dd><p>A pixel or region in the image used as the <strong>starting point</strong> for growing a potential ROI.\nSeeding is based on high-intensity regions in the filtered image, and forms the initial guess\nfor the shape and location of a neuron.</p></dd>", "a[href=\"#term-meanImgE\"]": "<dt id=\"term-meanImgE\">meanImgE</dt><dd><p>Enhanced mean image generated by Suite2p using filtering and sharpening operations. Useful for anatomical segmentation when available.</p></dd>", "a[href=\"#term-anatomical-segmentation\"]": "<dt id=\"term-anatomical-segmentation\">anatomical segmentation</dt><dd><p>ROI detection based solely on static images (e.g. mean or max projection), ignoring fluorescence time dynamics.</p></dd>", "a[href=\"#term-Delta-F-over-F0\"]": "<dt id=\"term-Delta-F-over-F0\">Delta F over F0</dt><dd><p>$\\Delta F / F_0$ (Delta F over F naught) refers to the fractional change in fluorescence relative to a baseline signal F\u2080.\nThis is a common normalization used in calcium imaging and neural activity recordings.</p></dd>", "a[href=\"#term-source-extraction\"]": "<dt id=\"term-source-extraction\">source-extraction</dt><dd><p>Umbrella term for all of the individual processes that produce a segmented image.</p></dd>", "a[href=\"#term-region-of-interest\"]": "<dt id=\"term-region-of-interest\">region-of-interest</dt><dd><p>A set of 1 or more 2D planes which are stitched together to form the full image.</p></dd>", "a[href=\"#term-Non-rigid-registration\"]": "<dt id=\"term-Non-rigid-registration\">Non-rigid-registration</dt><dd><p>The object is moved and transforms shape or size.</p></dd>", "a[href=\"#term-Field-of-view\"]": "<dt id=\"term-Field-of-view\">Field-of-view</dt><dd><p>The total size (um) of the raw stitched image with no trimming operations.</p></dd>", "a[href=\"#term-max_proj\"]": "<dt id=\"term-max_proj\">max_proj</dt><dd><p>The maximum value per pixel over time. Highlights regions with high fluorescence, useful for detecting active areas.</p></dd>", "a[href=\"#term-segmentation\"]": "<dt id=\"term-segmentation\">segmentation</dt><dd><p>The general process of dividing an image based on the contents of that image, in our case, based on neuron location.</p></dd>", "a[href=\"#term-Rigid-registration\"]": "<dt id=\"term-Rigid-registration\">Rigid-registration</dt><dd><p>The object retains shape and size.</p></dd>", "a[href=\"#term-functional-segmentation\"]": "<dt id=\"term-functional-segmentation\">functional segmentation</dt><dd><p>ROI detection based on pixelwise correlations across time, identifying spatially contiguous regions with similar activity patterns.</p></dd>", "a[href=\"#term-num_pixel_xy\"]": "<dt id=\"term-num_pixel_xy\">num_pixel_xy</dt><dd><p>The number of pixels [X, Y] in each <a class=\"reference internal\" href=\"#term-region-of-interest\"><span class=\"xref std std-term\">region-of-interest</span></a>.</p></dd>", "a[href=\"#term-roi_width_px\"]": "<dt id=\"term-roi_width_px\">roi_width_px</dt><dd><p>The size of the <a class=\"reference internal\" href=\"#term-region-of-interest\"><span class=\"xref std std-term\">region-of-interest</span></a> on its shortest dimension.</p></dd>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
